import React, { useState, useEffect } from 'react'
import ReactDOM from 'react-dom';
import { withStyles, makeStyles } from '@material-ui/core/styles';
import {Table,TableHead,TableContainer,TableBody,TableCell,TableRow,Chip,Card,Grid,Box,Paper, Typography,TextField, Button,CircularProgress} from '@material-ui/core';
import { Tabs, Tab, Row, Col, Nav } from 'react-bootstrap';
import './general.css'
import logo from './logo.png'
import {CONTRACT_ADDRESS,ABI } from "../config.js"
import Web3 from 'web3'

const web3 = new Web3(Web3.givenProvider)
const contract =  new web3.eth.Contract(ABI,CONTRACT_ADDRESS);


const PaientRecords = () => {
  const StyledTableCell = withStyles((theme) => ({
    head: {
      backgroundColor: theme.palette.info.main,
      color: theme.palette.common.blue,
    },
    body: {
      fontSize: 14,
    },
  }))(TableCell);

  const StyledTableRow = withStyles((theme) => ({
    root: {
      '&:nth-of-type(odd)': {
        backgroundColor: theme.palette.action.hover,
      },
    },
  }))(TableRow);

  const useStyles = makeStyles({
    table: {
      minWidth: 700,
    },
  });


  return (
    <Box mt={3} mb={3}>
    <TableContainer component={Paper}>   
    <Table size={"small"}>
        <TableHead>
            <TableRow>
                <StyledTableCell>
                    Doctor Name
                </StyledTableCell>
                <StyledTableCell>
                    Reason
                </StyledTableCell>
                <StyledTableCell>
                    Visited Date
                </StyledTableCell>
                <StyledTableCell>
                    Report
                </StyledTableCell>
            </TableRow>
        </TableHead>
        <TableBody>
            {/* {rows.map(
                (row,index)=>{
                    return(<StyledTableRow key={index}>
                        <TableCell>{row["dname"]}</TableCell>
                        <TableCell>{row["reason"]}</TableCell>
                        <TableCell>{row["visDte"]}</TableCell>
                        <TableCell><a href={"/#/embed/"+row["ipfs"]} target="_blank">View/Download Record</a></TableCell>
                    </StyledTableRow>)
                }
            )} */}


        </TableBody>
    </Table>
    </TableContainer> 
    </Box>

  );

}






