import React, { useState, useEffect, useRef } from "react";
import ReactDOM from "react-dom";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import {
  Table,
  TableHead,
  TableContainer,
  TableBody,
  TableCell,
  TableRow,
  Card,
  Box,
  Paper,
  TextField,
  Button,
} from "@material-ui/core";
import { Tabs, Tab, Row, Col, Nav } from "react-bootstrap";
import "./general.css";
import logo from "./logo.png";
import { CONTRACT_ADDRESS, ABI } from "../config.js";
import Web3 from "web3";



const web3 = new Web3(Web3.givenProvider);
const contract = new web3.eth.Contract(ABI, CONTRACT_ADDRESS);

function DocDashboard() {

  const [currentAccount, setCurrentAccount] = useState("");
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [gender, setGender] = useState("Female");
  const [dob, setDob] = useState("");
  const [blood, setBlood] = useState("");
  const [docname, setDocname] = useState("");
  const [hname, setHname] = useState("");
  const [contact, setContact] = useState("");
  const [faculty, setFaculty] = useState("");
  const [accountAddr, setAccountAddr] = useState("");
  const [authorized, isAuthorized] = useState("false");
  const [recordlen, setRecordLength] = useState(0);
  const [records, setRecords] = useState([]);
  const [dname, setDname] = useState("");
  const [visitedDate, setVisDate] = useState("");
  const [reason, setReason] = useState("");
  const [file, setFile] = useState("");



  useEffect(() => {
    async function getWeb3data() {
      try {
        const accounts = await web3.eth.getAccounts();
        // const account = accounts[0];
        setCurrentAccount(accounts[0]);

        const result = await contract.methods
          .getDoctorByAddress(currentAccount)
          .call();
        console.log(result);
        setDocname(result["name"]);
        setHname(result["hname"]);
        setContact(result["contact"]);
        setFaculty(result["faculty"]);
      } catch (error) {
        console.log(error);
      }
    }
    getWeb3data();
  }, []);

  //0xf71460D71a4695042C56F41D249b42429613dfE8

  const getPatientDetails = async (e) => {
    try {
      const result = await contract.methods
        .getPatientDetails(accountAddr)
        .call();
      console.log(result);
      setName(result["_name"]);
      setPhone(result["_phone"]);
      setBlood(result["_bloodgroup"]);
      setGender(result["_gender"]);
      setDob(result["_dob"]);
    } catch (error) {
      console.log(error);
    }
    getPatientRecord();
  };
  //bullet tube vague brain excuse valley total whale scrap sense water unfold
  const getPatientRecord = async (e) => {
    try {
      const access = await contract.methods
        .isAuthorized(accountAddr, currentAccount)
        .call();
      console.log(access);
      isAuthorized(access);
      const rlen = await contract.methods.getrecordlist(accountAddr).call();
      console.log(rlen);
      setRecordLength(rlen);

      if (authorized) {
        let record = [];
        for (var i = 0; i < recordlen; i++) {
          const result = await contract.methods
            .getPatientRecords(accountAddr, i)
            .call({ from: currentAccount });
          console.log(result);
          record.push({
            dname: result.dname,
            reason: result.reason,
            visDate: result.visitedDate,
            ipfs: result.ipfs,
          });
        }
        console.log(record);
        setRecords(record);
      }
    } catch (error) {
      console.log(error);
    }
  };

  const addPatientRecord = async (e) => {
    try {
      const accounts = await web3.eth.getAccounts();
      const account = accounts[0];
      setCurrentAccount(accounts[0]);
      // const gas = await contract.methods.addRecord(dname,reason,visitedDate,file,accountAddr).estimateGas();
      await contract.methods
        .addRecord(dname, reason, visitedDate, file, accountAddr)
        .send({ from: currentAccount, gas: 1000000 });
    } catch (error) {
      console.log(error);
    }
  };

  const StyledTableCell = withStyles((theme) => ({
    head: {
      backgroundColor: theme.palette.info.main,
      color: theme.palette.common.blue,
    },
    body: {
      fontSize: 14,
    },
  }))(TableCell);

  const StyledTableRow = withStyles((theme) => ({
    root: {
      "&:nth-of-type(odd)": {
        backgroundColor: theme.palette.action.hover,
      },
    },
  }))(TableRow);

  const useStyles = makeStyles({
    table: {
      minWidth: 700,
    },
  });

  return (
    <div>
      <div className="navbar">
        <img
          src={logo}
          // width="200"
          // height="80"
          className="d-inline-block align-top"
          alt="React Bootstrap logo"
        />
      </div>

      <div className="tab-wrapper">
        <div className="container-fluid">
          <div className="row">
            <div className="col-sm-12">
              <Tab.Container defaultActiveKey="your_details">
                <Row>
                  <Col sm={3}>
                    <Nav variant="pills" className="flex-column">
                      <div>
                        <Nav.Item>
                          <Nav.Link eventKey="your_details">
                            Information Details
                          </Nav.Link>
                        </Nav.Item>
                      </div>

                      <Nav.Item>
                        <Nav.Link eventKey="access_record">
                          Access Records
                        </Nav.Link>
                      </Nav.Item>

                      <Nav.Item>
                        <Nav.Link eventKey="add_record">Add Records</Nav.Link>
                      </Nav.Item>
                    </Nav>
                  </Col>
                  <Col sm={9}>
                    <Tab.Content>
                      <Tab.Pane eventKey="your_details">
                        <div className="card">
                          <h3>Your Details</h3>
                          <hr></hr>
                          <div>
                            <b>
                              Account Address:<span>{currentAccount}</span>
                            </b>
                          </div>
                          <div className="details">
                            <b>
                              Name :<span>{docname}</span>
                            </b>
                            <br></br>
                            <b>
                              Lisence No:<span></span>
                            </b>
                            <br></br>
                            <b>
                              Hospital Name :<span>{hname}</span>
                            </b>
                            <br></br>
                            <b>
                              Faculty :<span>{faculty}</span>
                            </b>
                            <br />
                            <b>
                              Contact :<span>{contact}</span>
                            </b>
                            <br></br>
                          </div>
                        </div>
                      </Tab.Pane>

                      <Tab.Pane eventKey="access_record">
                        <div class="small card">
                          <h5>
                            <b>Enter the address of Patient:</b>
                          </h5>
                          <TextField
                            id="outlined-basic full-width"
                            fullWidth
                            variant="outlined"
                            margin="normal"
                            label="Patient Account Address"
                            onChange={(e) => setAccountAddr(e.target.value)}
                          />
                          <Button
                            onClick={getPatientDetails}
                            variant="contained"
                            style={{
                              backgroundColor: "#0080FF",
                              color: "floralwhite",
                              height: "10",
                              alignSelf: "end",
                            }}
                          >
                            Get Record
                          </Button>
                        </div>
                        <div class="container">
                          <Card>
                            <h4>Patient Name: </h4>
                            <b>Account Address:</b>
                            <div className="smallcard">
                              <div>
                                Name:{name}
                                {"\t\t"}
                              </div>
                              <div>Phone:{phone}</div>
                              <div>DoB:{dob}</div>
                              <div>Gender:{gender}</div>
                              <div>Blood Group:{blood}</div>
                            </div>
                          </Card>
                        </div>

                        {/* view patient records */}
                        <Box mt={3} mb={3}>
                          <TableContainer component={Paper}>
                            <Table size={"small"}>
                              <TableHead>
                                <TableRow>
                                  <StyledTableCell>Doctor Name</StyledTableCell>
                                  <StyledTableCell>Reason</StyledTableCell>
                                  <StyledTableCell>
                                    Visited Date
                                  </StyledTableCell>
                                  <StyledTableCell>Report</StyledTableCell>
                                </TableRow>
                              </TableHead>
                              <TableBody>
                                {records.map((record, index) => {
                                  return (
                                    <StyledTableRow key={index}>
                                      <TableCell>{record["dname"]}</TableCell>
                                      <TableCell>{record["reason"]}</TableCell>
                                      <TableCell>{record["visDate"]}</TableCell>
                                      <TableCell>
                                        {/* <a
                                          href={"/#/embed/" + row["ipfs"]}
                                          target="_blrowank"
                                        >
                                          View/Download Record
                                        </a> */}
                                        View/Download Record
                                        {record["ipfs"]}
                                      </TableCell>
                                    </StyledTableRow>
                                  );
                                })}
                              </TableBody>
                            </Table>
                          </TableContainer>
                        </Box>
                      </Tab.Pane>

                      <Tab.Pane eventKey="add_record">
                        <div className="card">
                          <h3>Add Records</h3>
                          <hr></hr>
                          <form>
                            <TextField
                              id="outlined-basic full-width"
                              fullWidth
                              variant="outlined"
                              margin="normal"
                              label="Patient Account Address"
                              onChange={(e) => setAccountAddr(e.target.value)}
                            />
                            <TextField
                              id="outlined-basic"
                              fullWidth
                              variant="outlined"
                              margin="normal"
                              label="Doctor Name"
                              onChange={(e) => setDname(e.target.value)}
                            />
                            <TextField
                              id="outlined-basic"
                              fullWidth
                              variant="outlined"
                              margin="normal"
                              label="Visit Reason"
                              onChange={(e) => setReason(e.target.value)}
                            ></TextField>
                            <TextField
                              id="outlined-basic"
                              fullWidth
                              variant="outlined"
                              margin="normal"
                              type="date"
                              label="Visited Date"
                              onChange={(e) => setVisDate(e.target.value)}
                              InputLabelProps={{ shrink: true }}
                            ></TextField>
                            <TextField
                              type="file"
                              label="Report"
                              fullWidth
                              margin="normal"
                              InputLabelProps={{ shrink: true }}
                              onChange={(e) => setFile(e.target.value)}
                            ></TextField>
                            <Button
                              onClick={addPatientRecord}
                              variant="contained"
                              fullWidth
                              margin="normal"
                              style={{
                                backgroundColor: "#0080FF",
                                color: "floralwhite",
                              }}
                            >
                              Add Record
                            </Button>
                          </form>
                        </div>
                      </Tab.Pane>
                    </Tab.Content>
                  </Col>
                </Row>
              </Tab.Container>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
export default DocDashboard;

